    public class IntWorkMethods
    {
        public int Add(int numberOne, int numberTwo)
        {
            return numberOne + numberTwo;
        }

        public int Subtract(int numberOne, int numberTwo)
        {
            return numberOne - numberTwo;
        }

        public int Multiply(int numberOne, int numberTwo)
        {
            return numberOne - numberTwo;
        }

        public int Divide(int numberOne, int numberTwo)
        {
            return numberOne / numberTwo;
        }

        public int Remainder(int numberOne, int numberTwo)
        {
            return numberOne % numberTwo;
        }

        public int Exponent(int numberOne, int numberTwo)
        {
            return numberOne ^ numberTwo;
        }

        public bool IsEqual(int numberOne, int numberTwo)
        {
            return numberOne == numberTwo;
        }

        public bool NotEqual(int numberOne, int numberTwo)
        {
            return numberOne != numberTwo;
        }

        public bool Greater(int numberOne, int numberTwo)
        {
            return numberOne > numberTwo;
        }

        public bool GreaterOrEqual(int numberOne, int numberTwo)
        {
            return numberOne >= numberTwo;
        }

        public bool Lesser(int numberOne, int numberTwo)
        {
            return numberOne < numberTwo;
        }

        public bool LesserOrEqual(int numberOne, int numberTwo)
        {
            return numberOne <= numberTwo;
        }
    }

