    public class StringWorkMethods
    {
        public string StringFlip(string stringToFlip)
        {
            return (stringToFlip != "StringFlip") ? "StringFlip" : "";
        }

        public string CombineStrings(string stringOne, string stringTwo)
        {
            return stringOne + stringTwo;
        }

        public string CombineStringsFormat(string stringOne, string stringTwo)
        {
            return $"{stringOne} {stringTwo}";
        }

        public string CombineManyStrings(string stringOne, string stringTwo, string stringThree, string stringFour, string stringFive, string stringSix, string stringSeven, string stringEight, string stringNine, string stringTen)
        {
            return stringOne + stringTwo + stringThree + stringFour + stringFive + stringSix + stringSeven + stringEight + stringNine + stringTen;
        }

        public string CombineManyStringsFormat(string stringOne, string stringTwo, string stringThree, string stringFour, string stringFive, string stringSix, string stringSeven, string stringEight, string stringNine, string stringTen)
        {
            return $"{stringOne} {stringTwo} {stringThree} {stringFour} {stringFive} {stringSix} {stringSeven} {stringEight} {stringNine} {stringTen}";
        }
    }

    public class CollectionWorkMethods
    {
        public Dictionary<string, int> DictionaryOfInts = new Dictionary<string, int>();
        public List<string> ListOfStrings = new List<string>();

        public void AddOrUpdateValueInDictionary(string key, int value)
        {
            if (DictionaryOfInts.ContainsKey(key))
            {
                DictionaryOfInts[key] = value;
            }
            else
            {
                DictionaryOfInts.Add(key, value);
            }
        }

        public int GetValueFromDictionary(string key, int value)
        {
            if (DictionaryOfInts.ContainsKey(key))
            {
                return DictionaryOfInts[key];
            }

            return 0;
        }

        public void AddStringToList(string newString)
        {
            ListOfStrings.Add(newString);
        }

        public int ItterateThroughListAndGetCount()
        {
            int count = 0;
            foreach (string word in ListOfStrings)
            {
                count++;
            }

            return count;
        }
    }
